	{%player%.siroshi15} = 0

command /fishmaterial <player=%player%> <player=%player%>:
	usage: /fishmaterial(/fma)[]
	aliases: /fma
	trigger:
		command "/playsound minecraft:entity.player.attack.sweep ambient %arg 1% ~ ~ ~ 1.0 0.4 1.0"
		wait 2 tick
		open chest with 3 row named "&4&l%arg 2%&2&l &1&l[]" to arg 1
		wait 1 tick
		command "/playsound minecraft:entity.arrow.hit_player ambient %arg 1% ~ ~ ~ 1.0 0.5 1.0"
		set {%arg 1%.inv} to 0
		if {%arg 2%.} > 0:
			format slot {%arg 1%.inv} of arg 1 with fish named "&b" with lore "&a:&e1||&c||&a:&d%{%arg 2%.}%" to close
			add 1 to {%arg 1%.inv}
		if {%arg 2%.} > 0:
			format slot {%arg 1%.inv} of arg 1 with raw clownfish named "&b" with lore "&a:&e1||&c||&a:&d%{%arg 2%.}%" to close
			add 1 to {%arg 1%.inv}
		if {%arg 2%.} > 0:
			format slot {%arg 1%.inv} of arg 1 with fish named "&b" with lore "&a:&e2||&c||&a:&d%{%arg 2%.}%" to close
			add 1 to {%arg 1%.inv}
		if {%arg 2%.} > 0:
			format slot {%arg 1%.inv} of arg 1 with fish named "&b" with lore "&a:&e2||&c||&a:&d%{%arg 2%.}%" to close
			add 1 to {%arg 1%.inv}
		if {%arg 2%.} > 0:
			format slot {%arg 1%.inv} of arg 1 with raw salmon named "&b" with lore "&a:&e3||&c||&a:&d%{%arg 2%.}%" to close
			add 1 to {%arg 1%.inv}
		if {%arg 2%.} > 0:
			format slot {%arg 1%.inv} of arg 1 with quartz_block named "&b" with lore "&a:&e3||&c||&a:&d%{%arg 2%.}%" to close
			add 1 to {%arg 1%.inv}
		if {%arg 2%.} > 0:
			format slot {%arg 1%.inv} of arg 1 with fish named "&b" with lore "&a:&e4||&c||&a:&d%{%arg 2%.}%" to close
			add 1 to {%arg 1%.inv}
		if {%arg 2%.} > 0:
			format slot {%arg 1%.inv} of arg 1 with iron_horse_armour named "&b" with lore "&a:&e4||&c||&a:&d%{%arg 2%.}%" to close
			add 1 to {%arg 1%.inv}
		if {%arg 2%.} > 0:
			format slot {%arg 1%.inv} of arg 1 with raw pufferfish named "&b" with lore "&a:&e4||&c||&a:&d%{%arg 2%.}%" to close
			add 1 to {%arg 1%.inv}
		if {%arg 2%.} > 0:
			format slot {%arg 1%.inv} of arg 1 with fish named "&b" with lore "&a:&e4||&c||&a:&d%{%arg 2%.}%" to close
			add 1 to {%arg 1%.inv}
		if {%arg 2%.} > 0:
			format slot {%arg 1%.inv} of arg 1 with stone_sword named "&b" with lore "&a:&e5||&c||&a:&d%{%arg 2%.}%" to close
			add 1 to {%arg 1%.inv}
		if {%arg 2%.} > 0:
			format slot {%arg 1%.inv} of arg 1 with blue_glazed_terracotta named "&b" with lore "&a:&e5||&c||&a:&d%{%arg 2%.}%" to close
			add 1 to {%arg 1%.inv}
		if {%arg 2%.} > 0:
			format slot {%arg 1%.inv} of arg 1 with gold_horse_armour named "&b" with lore "&a:&e5||&c||&a:&d%{%arg 2%.}%" to close
			add 1 to {%arg 1%.inv}
		if {%arg 2%.Kurumi1011} > 0:
			format slot {%arg 1%.inv} of arg 1 with player head named "&bKurumi1011" with lore "&a:&e5||&c||&a:&d%{%arg 2%.Kurumi1011}%" to close
			add 1 to {%arg 1%.inv}
		if {%arg 2%.siroshi15} > 0:
			format slot {%arg 1%.inv} of arg 1 with player head named "&bsiroshi15" with lore "&a:&e???||&c||&a:&d%{%arg 2%.siroshi15}%" to close
			add 1 to {%arg 1%.inv}
		stop

on click with fish:
	command "/fishing %player%"
	remove 1 of held item from player

on click with raw salmon:
	command "/fishing %player%"
	remove 1 of held item from player

on click with raw clownfish:
	command "/fishing %player%"
	remove 1 of held item from player

on click with raw pufferfish:
	command "/fishing %player%"
	remove 1 of held item from player

on fishing:
	message "&e"

command /fishing <player>:
	permission: admin
	trigger:
		set {_fishing} to a random integer between 1 and 260
		command "/playsound minecraft:entity.player.levelup ambient %arg 1% ~ ~ ~ 1.0 0.53 1.0"
		if {_fishing} > 0:
			if {_fishing} < 50:
				message "&b&l&a &e[]" to arg 1
				give arg 1 349 named "&a[]" with lore "&a[&e&a]||&b:&d"
		if {_fishing} > 49:
			if {_fishing} < 100:
				message "&b&l&a &e[]" to arg 1
				give arg 1 raw clownfish named "&a[]" with lore "&a[&e&a]||&b:&d"
		if {_fishing} > 99:
			if {_fishing} < 130:
				message "&b&l&a &e[]" to arg 1
				give arg 1 349 named "&a[]" with lore "&a[&e&a]||&b:&d"
		if {_fishing} > 129:
			if {_fishing} < 160:
				message "&b&l&a &e[]" to arg 1
				give arg 1 349 named "&a[]" with lore "&a[&e&a]||&b:&d"
		if {_fishing} > 159: 
			if {_fishing} < 175:
				message "&b&l&a &e[]" to arg 1
				give arg 1 raw salmon named "&a[]" with lore "&a[&e&a]||&b:&d"
		if {_fishing} > 174:
			if {_fishing} < 190:
				message "&b&l&a &e[]" to arg 1
				give arg 1 quartz_block named "&a[]" with lore "&a[&e&a]||&b:&d"
		if {_fishing} > 189:
			if {_fishing} < 200:
				message "&b&l&a &e[]" to arg 1
				give arg 1 349 named "&a[]" with lore "&a[&e&a]||&b:&d"
		if {_fishing} > 199:
			if {_fishing} < 210:
				message "&b&l&a &e[]" to arg 1
				give arg 1 iron_horse_armour named "&a[]" with lore "&a[&e&a]||&b:&d"
		if {_fishing} > 209:
			if {_fishing} < 220:
				message "&b&l&a &e[]" to arg 1
				give arg 1 raw pufferfish named "&a[]" with lore "&a[&e&a]||&b:&d"
		if {_fishing} > 219:
			if {_fishing} < 230:
				message "&b&l200Gold&a &e[]  %{%arg 1%.g}%G" to arg 1
				add 200 to {%arg 1%.g}
		if {_fishing} > 229:
			if {_fishing} < 235:
				message "&b&l&a &e[]" to arg 1
				give arg 1 350 named "&a[]" with lore "&a[&e&a]||&b:&d"
		if {_fishing} > 234:
			if {_fishing} < 240:
				message "&b&l&a &e[]" to arg 1
				give arg 1 stone_sword named "&a[]" with lore "&a[&e&a]||&b:&d"
				broadcast "&e&l%arg 1%&a&d&a "
		if {_fishing} > 239:
			if {_fishing} < 245:
				message "&b&l&a &e[]" to arg 1
				give arg 1 blue_glazed_terracotta named "&a[]" with lore "&a[&e&a]||&b:&d"
				broadcast "&e&l%arg 1%&a&d&a "
		if {_fishing} > 244:
			if {_fishing} < 250:
				message "&b&l&a &e[]" to arg 1
				give arg 1 gold_horse_armour named "&a[]" with lore "&a[&e&a]||&b:&d"
				broadcast "&e&l%arg 1%&a&d&a "
		if {_fishing} is 250:
			message "&b&l&aKurumi1011 &e[]" to arg 1
			give arg 1 skull of Kurumi1011 named "&a[Kurumi1011]" with lore "&a[&e&a]||&b:&d"
			broadcast "&e&l%arg 1%&a&dKurumi1011&a "
		if {_fishing} > 249:
			if {_fishing} < 260:
				message "&b&l&asiroshi15 &e[]" to arg 1
				give arg 1 skull of siroshi15 named "&a[siroshi15]" with lore "&a[&e&a]||&b:&d"
				broadcast "&e&l%arg 1%&a&dsiroshi15&a "
