public class Factorial{
	private int fieldNumber;
	private int fieldResult = 1; 


	public Factorial(){

	}

	public void setNumber(int fn){
		fieldNumber = fn;
	}
	public void calcFac(){
		for(int i=1;i<=fieldNumber;i++){    
      		fieldResult = fieldResult*i; 
 		}    
 	System.out.printf("Factorial Result : %d",fieldResult);   
 	}
}
