//Oh no
import becker.robots.*;
public class OhNo extends RobotSE {
	public OhNo(City city, int aStreet, int anAvenue, Direction aDirection) {
		super(city, aStreet, anAvenue, aDirection);
	}
	public void turnLeft() {
		this.turnLeft();
	}
}
//Main class
import becker.robots.*;
public class Main {
	public static void main(String args[]) {
		City Mississauga = new City();
		OhNo Robot = new OhNo(Mississauga,1,1,Direction.EAST);
		Robot.turnLeft();
	}
}
