std::string task5(std::string input) {
	int plus = 0;
	int minus = 0;
	int star = 0;
	for (int i = 0; i < input.length; i++) {
		if (input[i] == '+') plus++;
		if (input[i] == '-') minus++;
		if (input[i] == '*') star++;
	}
	return "+ - " + std::to_string(plus) + "\n - - " + std::to_string(minus) + "\n star - " + std::to_string(star);
}
