using System;
using System.Linq;
using System.Collections.Generic;
using System.Numerics;
using System.Globalization;
using System.Text;

namespace ConsoleApp3
{
    class Program
    {
        static void Main(string[] args)
        {
            int n = int.Parse(Console.ReadLine());

            //drink -> price
            var nameAndPrice = new Dictionary<string,double>();
            //name -> drink -> price
            var namesAndTasks = new Dictionary<string, Dictionary<string, int>>();

            for (int i = 0; i < n; i++)
            {
                string[] input = Console.ReadLine().Split(new char[] { '-' });
                string name = input[0];
                double price = double.Parse(input[1],CultureInfo.InvariantCulture);

                if (!nameAndPrice.ContainsKey(name))
                {
                    nameAndPrice.Add(name, price);
                }
                else
                {
                    nameAndPrice[name] = price;
                }
            }

            while (true)
            {
                string[] input = Console.ReadLine().Split(new char[] { '-' , ',' });
                string name = input[0];

                if(name == "end of clients") { break; }

                string drink = input[1];
                int count = int.Parse(input[2]);

                var currentDic = new Dictionary<string,int>();
                currentDic.Add(drink,count);

                if (!namesAndTasks.ContainsKey(name))
                {
                    if (nameAndPrice.ContainsKey(drink))
                    {
                        namesAndTasks.Add(name, new Dictionary<string, int>());
                        namesAndTasks[name] = currentDic;
                    }
                }
                else
                {
                    if (!namesAndTasks[name].ContainsKey(drink))
                    {
                        namesAndTasks[name].Add(drink, count);
                    }
                    else
                    {
                        namesAndTasks[name][drink] += count;
                    }
                }
            }

            double totalBill = 0;

            foreach(var person in namesAndTasks.OrderBy(x => x.Key))
            {
                Console.WriteLine(person.Key);
                double currentBill = 0;
                foreach(var drinks in person.Value)
                {
                    Console.WriteLine($"-- {drinks.Key} - {drinks.Value}");
                    currentBill += drinks.Value * nameAndPrice[drinks.Key];
                }
                Console.WriteLine($"Bill: {currentBill:F2}");
                totalBill += currentBill;
            }

            Console.WriteLine($"Total bill: {totalBill:F2}");
        }
    }
}
