package spawnpackage;

import org.bukkit.Bukkit;
import org.bukkit.Location;
import org.bukkit.World;
import org.bukkit.command.Command;
import org.bukkit.command.CommandSender;
import org.bukkit.entity.Player;
import org.bukkit.event.EventHandler;
import org.bukkit.event.player.PlayerRespawnEvent;
import org.bukkit.plugin.java.JavaPlugin;

public class Main extends JavaPlugin {

	@Override
	public void onEnable() {

		saveDefaultConfig();
		reloadConfig();

	}

	@Override
	public boolean onCommand(CommandSender sender, Command cmd, String string, String[] args) {

		if (sender instanceof Player) {

			Player p = (Player) sender;

			if (string.equalsIgnoreCase("spawn")) {

				if (getConfig().getString("spawn").equalsIgnoreCase("aucun")) {
					sender.sendMessage("7Aucun spawn dfinie");
				} else {

					String[] spawn = getConfig().getString("spawn").split(" ");

					World world = Bukkit.getWorld(spawn[0]);
					double x = Double.parseDouble(spawn[1]);
					double y = Double.parseDouble(spawn[2]);
					double z = Double.parseDouble(spawn[3]);

					Location l = new Location(world, x, y, z);

					p.teleport(l);

				}

			} else {

				if (args.length == 0) {

					String spawn = p.getWorld().getName();
					spawn += " " + p.getLocation().getX();
					spawn += " " + p.getLocation().getY();
					spawn += " " + p.getLocation().getZ();

					getConfig().set("spawn", spawn);

					p.sendMessage("7Le spawn a t chang au monde 6l" + p.getWorld().getName()
							+ " 7et les coordonnes sont:");
					p.sendMessage("7X: 6l" + p.getLocation().getX());
					p.sendMessage("7Y: 6l" + p.getLocation().getY());
					p.sendMessage("7Z: 6l" + p.getLocation().getZ());

				} else {

					if (args[0].equalsIgnoreCase("aucun")) {

						getConfig().set("spawn", "aucun");

						p.sendMessage("7Le spawn point du spawn a t retir");

					} else if (Bukkit.getPlayer(args[0]).getName().equalsIgnoreCase(args[0])) {

						Player p1 = Bukkit.getPlayer(args[0]);

						String spawn = p1.getWorld().getName();
						spawn += " " + p1.getLocation().getX();
						spawn += " " + p1.getLocation().getY();
						spawn += " " + p1.getLocation().getZ();

						getConfig().set("spawn", spawn);

						p.sendMessage("7Le spawn a t chang au monde 6l" + p.getWorld().getName()
								+ " 7et les coordonnes sont:");
						p.sendMessage("7X: 6l" + p.getLocation().getX());
						p.sendMessage("7Y: 6l" + p.getLocation().getY());
						p.sendMessage("7Z: 6l" + p.getLocation().getZ());

					} else {

						p.sendMessage("7Impossible de trouver le joueur 6l" + args[0]);

						if (Bukkit.getOnlinePlayers().isEmpty())
							p.sendMessage("7Aucun joueur n'est connect!");
						else if (Bukkit.getOnlinePlayers().size() == 1) {

							for (Player p1 : Bukkit.getOnlinePlayers())
								p.sendMessage("7Un seul joueur est connect et c'est 6l" + p1.getName());

						} else {

							p.sendMessage("7Voici la liste des joueurs connects:");
							for (Player p1 : Bukkit.getOnlinePlayers())
								p.sendMessage("7- 6l" + p1.getName());

						}

					}

				}

			}

		}

		return true;

	}

	@EventHandler
	public void onrespawn(PlayerRespawnEvent e) {

		if (!getConfig().getString("spawn").equalsIgnoreCase("aucun")) {

			String[] spawn = getConfig().getString("spawn").split(" ");

			World world = Bukkit.getWorld(spawn[0]);
			double x = Double.parseDouble(spawn[1]);
			double y = Double.parseDouble(spawn[2]);
			double z = Double.parseDouble(spawn[3]);

			Location l = new Location(world, x, y, z);

			e.getPlayer().teleport(l);

		}

	}

}
