        cout<<"\n2." + druga.nazwa + " " + druga.autor;
        cout<<"\n3." + trzecia.nazwa + " " + trzecia.autor;
        cout<<"\n4." + czwarta.nazwa + " " + czwarta.autor;

            cout<<"wybierz numer przypisany do ksiazki: ";








