#include <stdio.h>
#include <stdlib.h>
double wurz(double a , double b ,
 double c , double *x1 ,double *x2);
int main()
{
    double a,b,c,x1,x2;
    printf("sayi ver");
    scanf("%lf %lf %lf",&a,&b,&c);
    wurz(a,b,c,&x1,&x2);
    if(!wurz(a,b,c,&x1,&x2))
    {
        printf("reele kok yok");
    }
    else
    {
    printf("x1=%f",x1);
    printf("x2=%f",x2);
    }
    return 0;
}
 double wurz(double a , double b , double c 
, double *x1 ,double *x2)
{
    double wurzel;
    wurzel = pow(b,2)-4*a*c ;
    if(wurzel<0)
    {
    return 0;
    }
    else
    {
    *x1=(b-sqrt(wurzel))/(2*a) ;
    *x2=(b+sqrt(wurzel))/(2*a) ;
}
}
