def caesar(chars):
	for i in range(len(chars)):
		if 65 <= ord(chars[i]) <= 90 or 97 <= ord(chars[i]) <= 122:
			chars = (chars[:i] + chars[i:].replace(chars[i], chr(ord(chars[i]) + 3), 1)) if (65 <= (ord(chars[i]) + 3) <= 90 or 97 <= (ord(chars[i])+3) <= 122) else (chars[:i] + chars[i:].replace(chars[i], chr(ord(chars[i]) - 23), 1))
	return chars

while True:
        print caesar(raw_input("GIMME: "))
