def odkodowanie():
	print ("Podaj zakodowany tekst w alfabecie Morse'a (znaki oddzielaj / a koniec sowa //):  ")
	L=input()
	A=L.split("/")
	L1=['t','e']
	L2=['m','n','a','i']
	L3=['o','g','k','d','w','r','u','s']
	L4=['#','#','q','z','y','c','x','b','j','p','#','l','#','f','v','h']
	for i in A[0:len(A)-2]:
		if i=='':
			print (" ",end='')
		if i != '':
			if len(i)==1:
				S=L1
			if len(i)==2:
				S=L2
			if len(i)==3:
				S=L3
			if len(i)==4:
				S=L4	
			m=0
			n=len(S)
			for j in i:
				if j=="-":
					n=(m+n)//2
				if j==".":
					m=(m+n)//2
			print(S[m],end='')		

