using System;
namespace geometrischeFiguren
{
    abstract class Figuren
    {
        public int[] length = new int[3];
        public string color;
        public string type;
    }
    class Dreieck:Figuren
    {
        public Dreieck()
        {
            base.type = "Rechteck";
            bool fehler = true;
            string color = "";
            do
            {
                Console.Write("Bitte trage eine Farbe ein: ");
                color = Console.ReadLine();
                if (!String.IsNullOrEmpty(color))
                    fehler = false;
            } while (fehler);
            base.color = color;

            int[] numbers = new int[2];
            for (int i = 0; i < numbers.Length; i++)
            {
                int number = 0;
                fehler = true;
                do
                {
                    Console.WriteLine("Bitte trage die {0}. Gre ein: ", i + 1);
                    bool result = Int32.TryParse(Console.ReadLine(), out number);
                    if (result)
                        fehler = false;

                } while (fehler);
                numbers[i] = number;
            }
            base.length = numbers;
        }
    }
    class Rechteck : Figuren
    {
        public Rechteck()
        {
            base.type = "Rechteck";
            bool fehler = true;
            string color = "";
            do
            {
                Console.Write("Bitte trage eine Farbe ein: ");
                color = Console.ReadLine();
                if (!String.IsNullOrEmpty(color))
                    fehler = false;
            } while (fehler);
            base.color = color;

            int[] numbers = new int[3];
            for (int i = 0; i < 3; i++)
            {
                int number = 0;
                fehler = true;
                do
                {
                    Console.WriteLine("Bitte trage die {0}. Gre ein: ", i + 1);
                    bool result = Int32.TryParse(Console.ReadLine(), out number);
                    if (result)
                        fehler = false;

                } while (fehler);
                numbers[i] = number;
            }
            base.length = numbers;

        }
    }
    class Kreis : Figuren
    {
        public Kreis()
        {
            base.type = "Kreis";
            bool fehler = true;
            string color = "";
            do
            {
                Console.Write("Bitte trage eine Farbe ein: ");
                color = Console.ReadLine();
                if (!String.IsNullOrEmpty(color))
                    fehler = false;
            } while (fehler);
            base.color = color;

            int number = 0;
            fehler = true;
            do
            {
                Console.WriteLine("Bitte trage die Gre ein: ");
                bool result = Int32.TryParse(Console.ReadLine(), out number);
                if (result)
                    fehler = false;

            } while (fehler);
            base.length[0] = number;

        }
    }
    class Program
    {
        static void Main(string[] args)
        {
            Dreieck testMe = new Dreieck();
            Rechteck lol = new Rechteck();
            Kreis hmm = new Kreis();
        }
    }
}
