import javax.swing.*;

class oppg2c{

	public static void main(String[] args) {


		String input1 = JOptionPane.showInputDialog("Skriv inn en tekst");
		String input2 = JOptionPane.showInputDialog("Skriv inn en tekst til");


		boolean resultat = delStreng(input1, input2);

		JOptionPane.showMessageDialog(null,"Er tekst nr 2 en delstreng i tekst nr 1? "+resultat);

	}

	public static boolean delStreng(String t1, String t2) {

		boolean resultat = false;
		int teller = 0;

		for (int i=0; i<t1.length();i++){

			teller++;
			int k = teller + t2.length();
			String temp = t1.substring(i, k);

			if (temp == t2){
				resultat = true;
			}

		}
		return resultat;		
	}		
}
