CMD:dijelibroj(playerid, params[])
{
	new string[50], broj1, broj2, rezultat;
	if(sscanf(params, "i","i" broj1, broj2))
	{
	    SendClientMessage(playerid, -1, ""BALKAN"[B:DM]"BJELA": /dijelibroj [broj1] [broj1]");
	    return 1;
	}
	if(broj1 == 0 || broj2 == 0 || broj1 < broj2) return SCM(playerid, -1, ""BALKAN"[B:DM]"BJELA": Ne mozes dijeliti broj sa nulom, ili si stavio da je broj 1 veci od broja 2.");
	rezultat = broj1/broj2;
	format(string, sizeof(string), ""BALKAN"*"BJELA"Rezultat tvog dijeljenja jeste: %d", rezultat);
	return 1;
}
