using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading.Tasks;

namespace ConsoleHelloWorld
{
    class Program_new
    {
        static void Main(string[] args)
        {

            string[] arr1 = 
                new string[] { "zero", "one", "two", "three", "four", "five" };

            for (int i = 2; i < 5; i++)
            {
                Console.WriteLine("Value {0} translates to '{1}'", i, arr1[i]);
            }

            Console.WriteLine("Value {0} translates to '{1}'", 2, arr1[2]);
            Console.WriteLine("Value {0} translates to '{1}'", 3, arr1[3]);
            Console.WriteLine("Value {0} translates to '{1}'", 4, arr1[4]);

            Console.ReadLine();
        }

    }
}
