import java.util.*;
import java.lang.*;
import java.io.*;
class Ideone
{
     static int[] doSelectionSort(int[] arr){
        for (int i = 0; i < arr.length - 1; i++)
        {
            int index = i;
            for (int j = i + 1; j < arr.length; j++)
                if (arr[j] < arr[index]) 
                    index = j;

            int smallerNumber = arr[index];  
            arr[index] = arr[i];
            arr[i] = smallerNumber;
        }
        return arr;
    }

    static int[][] sort(int[][] a){
    	int[][] res = new int[a.length][a[0].length];
    	int i =0;
    	for(int j = 0 ; j < a[0].length; ++j){
    		res[i] = doSelectionSort(a[i]);
    		i++; 
    	}
    	return res;
    }

	static int[][] createMatrix(int n, int m){
		int[][] a = new int[n][m];
		for(int i=0 ; i < n ; ++i){
			for(int j=0; j<m;++j){
				a[i][j] = (n+m)-(i+j);
			}
		}
		return a;
	}

	static void printMatrix(int[][] a, int n, int m){
		for(int i = 0 ; i < n ; ++ i){
			for(int j=0; j<m;++j){
				System.out.print(a[i][j]);
				System.out.print(" ");
			}
				System.out.print("\n");
		}
	}

	public static void main (String[] args) throws java.lang.Exception
	{
		int[][] a = createMatrix(3,3);
		printMatrix(a,3,3);
		System.out.print("\n");
		printMatrix(sort(a),3,3);
	}
}
