import java.util.Arrays;
import java.util.List;
import java.util.LinkedList;
import pirates.*;

public class objectsInfo implements PirateBot
{
        private int getNumOfMotherships(PirateGame game){
        int mnum;
        for(Mothership mothership : game.getMyMothership()){
            mnum++;
        }
        return mnum;
    }
        private int getNumOfEnemyMotherships(PirateGame game){
        int mnum;
        for(Mothership mothership : game.getEnemyMothership()){
            mnum++;
        }
        return mnum++;
    }
        private int getNumOfPirates(PirateGame game){
        int mnum;
        for(Pirate pirate : game.getMyLivingPirates()){
            mnum++;
        }
        return mnum++;
    }
        private int getNumOfEnemyPirates(PirateGame game){
        int mnum;
        for(Pirate pirate : game.getEnemyLivingPirates()){
            mnum++;
        }
        return mnum++;
    }
        private int getNumOfCapsules(PirateGame game){
        int mnum;
        for(Capsule pirate : game.getMyCapsules()){
            mnum++;
        }
        return mnum++;
    }
        private int getNumOfEnemyCapsules(PirateGame game){
        int mnum;
        for(Capsule pirate : game.getEnemyCapsules()){
            mnum++;
        }
        return mnum++;
    }
}
