#include<stdio.h>
int main(){
	char oras1[30],oras2[30],oras3[30];
	int s1, s2, s3, st;
	printf("Introduceti numele primului oras, precum si numarul de studenti din acel oras:");
	scanf("%s%d", oras1, &s1);
	printf("Introduceti numele celui de-al doilea oras, precum si numarul de studenti din acel oras:");
	scanf("%s%d", oras2, &s2);
	printf("Introduceti numele celui de-al treilea oras, precum si numarul de studenti din acel oras:");
	scanf("%s%d", oras3, &s3);

	st = s1 + s2 + s3;

	printf("Numarul total de studenti din cele trei orase este:%d", st);



	return 0;
}
