{% extends "base.html" %}
{% block container %}


    <div class="container">
    <div class="row">



      {% if schedules %}
       Tabela de Horrios Pilates
      <table class="table table-bordered">
      <thead class="thead-light">
        <tr>
          {% for week in weeks %}
          <th class="text-center" scope="col">{% if week.name %}{{ week.name }}{% else %}{% endif %}</th>
           {% endfor %}
        </tr>
      </thead>
        {% for schedule in schedules %}
      <tbody>

         <td class="text-center schedule">
              {% if schedule.week.name == "Segunda" %}
              {{ schedule.name  }} <br /> 
                                    {{ schedule.team }} <br />
                                    {{ schedule.start_hour }} s 
                                    {{ schedule.end_hour }} - 
                                    {{ schedule.week.name }}
              {% else %}
              {% endif %}
          </td>

           <td class="text-center schedule">
