#include<stdio.h>
#include<string.h>
char *strstr(char *a,char *b);
int main()
{
    char a[30],b[30],*c;
    gets(a);
    gets(b);
    c=strstr(a,b);
    puts(c);
}
char *strstr(char *p,char *q)
{
    char *a=p,*b=q,*c,*m,*n;
    int len1=strlen(a);
    int len2=strlen(b);
    int i,j,flag;
    for(i=0;i<len1;i++,a++)
    {
        if(*a!=*b)
        continue;
        flag=1;
        m=a;
        n=b;
        for(j=0;j<len2;j++,m++,n++)
        {
            if(*m!=*n)
            {
                flag=0;
                break;
            }
        }
        if(flag)
        return a;
    }
    return 0;
}
