using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;

namespace Projekat
{
    class Program
    {

        static void prikaziSkor(int brojT, int brojN, int bodovi)
        {
            Console.Clear();
            Console.WriteLine("Broj tacnih odgovora: "+brojT);
            Console.WriteLine("Broj netacnih odgovora: "+brojN);
            Console.WriteLine("*********************************\nBroj bodova: " + bodovi + "\n*********************************");
            Console.ReadKey();
        }
        static int dodajBodove(int trenutniBodovi,int dodatiBodovi){
            return trenutniBodovi + dodatiBodovi;
        }
        static int pocetniIspis() {
            Console.Clear();
            Console.WriteLine("***************** DOBRODOLI U KVIZ ZNANJA *****************");
            Console.WriteLine("1. Pocnite kviz");
            Console.WriteLine("2. Moji trenutni skor");
            Console.WriteLine("3. Zavri kviz");

            int opcija=0;
            try
            {
                opcija = Convert.ToInt32(Console.ReadLine());
            }
            catch {
                Console.WriteLine("Unos nije validan, pokuajte ponovo");
            }

            return opcija;

        }
        static void Main(string[] args)
        {

            int brojBodova = 0;
            int brojTacnih = 0;
            int brojNetacnih = 0;

            int[] bodoviPoPitanjima={1,2,4,2,5,3,5,4};
            String[] drzave={"Srbije", "Japana", "Velike Britanije", "Turske", "Nemacke", "Poljske", "Indije", "Francuske"};
            String[] glavniGradovi = { "Beograd", "Tokio", "London", "Ankara", "Berlin", "Varava", "Nju Delhi", "Pariz" };
            do
            {

                int unos = pocetniIspis();

                if (unos == 1)
                {
                    brojBodova = 0;
                    brojNetacnih = 0;
                    brojTacnih = 0;
                    for (int i = 0; i < drzave.Length; i++)
                    {
                        Console.Clear();
                        Console.WriteLine("Koji je glavni grad " + drzave[i] + "?");
                        String odgovor = Console.ReadLine();

                        if (odgovor.ToLower().Equals(glavniGradovi[i].ToLower()))
                        {
                            Console.WriteLine("******************\nOdgovor je taan!\n******************");
                            brojBodova = dodajBodove(brojBodova, bodoviPoPitanjima[i]);
                            brojTacnih++;


                        }
                        else
                        {
                            Console.WriteLine("******************\nOdgovor je netaan!\n******************");
                            brojNetacnih++;
                        }
                        Console.WriteLine("\nVai trenutni bodovi: " + brojBodova);
                        Console.WriteLine("\n\nZa povratak na pocetnu stranu pritisnite 0");
                        int br;
                        try
                        {
                            br = Convert.ToInt32(Console.ReadLine());
                            if (br == 0)
                            {
                                break;
                            }

                        }
                        catch
                        {
                            Console.WriteLine("Za povratak na pocetnu stranu pritisnite 0, a za sledece pitanje 1");
                        }

                    }

                }
                else if (unos == 2)
                {
                    prikaziSkor(brojTacnih, brojNetacnih, brojBodova);
                }
                else if (unos == 3)
                {
                    Environment.Exit(0);
                }
            } while (true);
        }
    }
}
