inventory = { 'arrow' : 12,
                     'gold coin' : 42,
                     'rope' : 1,
                     'torch' : 6,
                     'dagger' : 1 }

def displayInventory(inventory):
    total = 0
    for i in inventory:
        if inventory[i] < 2:
            print ("You have " + str(inventory[i]) + " " + i + ".")
        else:
            if i[-1] == "h":
                print ("You have " + str(inventory[i]) + " " + i + "es.")
            else:
                print ("You have " + str(inventory[i]) + " " + i + "s.")

    for i in inventory:
        total += inventory[i]
    print ()
    print ("You have " + str(total) + " items total in your inventory.")
