import java.util.*;

public class ArrayTraversal
{
	private int[] numbers;
	public ArrayTraversal (int... posInts)
	{
		numbers = posInts;
	}

	public int toInt()
	{
		Scanner input = new Scanner(System.in);

		System.out.println("Please enter some positive integers: ");
		while (input.hasNextInt()) {
			this.addNumber(input);
		}

		System.out.println("Your array is: " + this.number.toString());

		//List of all variables.
		int menuNum = 0;

		//Enters the menu.
		while(menuNum != 4)
		{
			//Prints the menu and offers the choice selection.
			System.out.println("1.Add a number to the array.\n2.Display the mean of the array.\n3.Display the median of the array.\n4.Quit the array.");
			System.out.println("Your choice please: ");
			menuNum = input.nextInt();

			if(menuNum == 1) {
			//Fulfills option one of the menu.
				System.out.println("Please enter the integer you'd like to add: ");
				this.addNumber(input);
			} else if (menuNum == 2) {
			//Fulfills option two of the menu by finding the mean.
				System.out.println("The mean is: " + this.getMean());
			} else if(menuNum == 3) {
			//Fulfills option three of the menu by finding the median.	
				System.out.println("The median is: " + this.getMedian());
			} else {
				//User is stupid and doesn't select 1-4
				System.out.println("Please select one of the menu options.";
			}
		}

		//Fulfills option four of the menu.	
		System.out.println("Thank you and have a nice day.");
	}

	private void addNumber(Scanner input) {
		System.out.println("Please enter the integer you'd like to add: "); 
		int current = input.getInt(); 
		while (current < 1) { 
			println('Please input a positive integer...') 
			current = input.getInt(); 
		}
		this.numbers.add(current);
	}

	private float getMedian() {
		//Median of empty array is 0 :)
		if (this.numbers.length == 0) return 0;

		//Sort array numerically.
		sort(this.numbers);
		boolean even = (this.numbers.length % 2 == 0);

		int index = (int) Math.floor(number.length/2);

		//The middle number is at index Math.floor(number.length/2);
		// 0 1 2 3 4 -> length = 5, 5/2 = 2.5, floor => 2
		float median = (float) numbers[index];
		if (even) {
			//There is no 'middle' number, so you need to take the average of the 2 middle ones.
			//0 1 2 3 4 5 -> Index 2 and 3, length = 6 so ([index] + [index] - 1) / 2
			median += (float) numbers[index - 1];
			median /= 2;
		}
		//Print it out nicely.
		return median;
	}

	private float getMean() {
		//Median of empty array is 0 :)
		if (this.numbers.length == 0) return 0;
		float mean = 0;
		for (int i = 0; i < this.numbers.length; i++) {
			mean += this.numbers[i];
		}
		mean /= this.numbers.length;
		return mean;
	}
}
