const React = require('react');
const Konva = require('konva');
const { Stage, Layer, Rect, Circle } = require('react-konva');

const CONSTANTS = require('./constants');

require('./layout-tab.css');

const LayoutTab = ({ activePws }) => {
  return (
    <div className='layout-tab'>
      <Stage className='pws-silhouette' width={400} height={600}>
        {/* PWS silhouette layer */}
        <Layer >
          <Rect
            x={0}
            y={0}
            width={400}
            height={600}
            fill={'#375362'}
          />
        </Layer>

        {/* Modules background layer */}
        <Layer>
          <Rect
            x={CONSTANTS.OFFSETS.MODULES_BACKGROUND_LAYER.X}
            y={CONSTANTS.OFFSETS.MODULES_BACKGROUND_LAYER.Y}
            width={340}
            height={540}
            fill={'#F2F6F8'}
          />
        </Layer>

        {/* PWS modules layer */}
        <Layer>
        {
          activePws.pwsStorageAreas[0].pwsModules.map(pwsModule => {
            return (
              <React.Fragment key={pwsModule.id}>
                <Rect
                  key={pwsModule.id}
                  x={Number(pwsModule.x) + CONSTANTS.OFFSETS.MODULES_BACKGROUND_LAYER.X}
                  y={Number(pwsModule.y) + CONSTANTS.OFFSETS.MODULES_BACKGROUND_LAYER.Y}
                  width={pwsModule.moduleTemplate.width}
                  height={pwsModule.moduleTemplate.height}
                  shadowBlur={3}
                  fill={'#cce3ff'}
                  onClick={() => alert(pwsModule.name)}
                />
                {
                  pwsModule.moduleTemplate.slots.map(slot => {
                    return (
                      <Circle
                        key={slot.name}
                        x={Number(pwsModule.x) + CONSTANTS.OFFSETS.MODULES_BACKGROUND_LAYER.X + slot.position.x}
                        y={Number(pwsModule.y) + CONSTANTS.OFFSETS.MODULES_BACKGROUND_LAYER.Y + slot.position.y}
                        radius={slot.radius}
                        fill={'#375362'}
                      />
                    )
                  })
                }
              </React.Fragment>
            )
          })
        }
        </Layer>
      </Stage>
    </div>
  )
};


module.exports = LayoutTab;
