package main

import (
	"fmt"
	"strings"
	"unicode"
)

func countLetters(s string) (result int) {
	for _, e := range s {
		if unicode.IsLetter(e) {
			result++
		}
	}
	return
}

func main() {
	s := "Now I need a drink, alcoholic of course, after the heavy lectures involving quantum mechanics."
	t := strings.Split(s, " ")
	result := make([]int, len(t))
	for i, e := range t {
		result[i] = countLetters(e)
	}
	fmt.Println(result)
}
